import React from 'react'

import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min";
import Dropdown from './Dropdown';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowDown, faArrowUp } from '@fortawesome/free-solid-svg-icons';




function Converter() {
    return (
        <div className='w-25 border m-auto mt-5 rounded-3 main bg-black text-white'>
            <h2 className='text-center pt-3'>Currency converter</h2>
            <div className='mt-5 px-5'>
               <Dropdown

               /> 
               <br/>
               <div className='text-center border text-center bg-white text-black w-25 rounded m-auto'>
                    <FontAwesomeIcon icon={faArrowDown} />
                    <FontAwesomeIcon icon={faArrowUp} />
               </div>
               <Dropdown 

               />
            </div>
            <div className='mt-5'>
                <label htmlFor="">Amount</label>
                <input type="text" />
            </div>
        </div>

    )
}

export default Converter
