import React from 'react'

import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min";
import Dropdown from './Dropdown';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowDown, faArrowUp } from '@fortawesome/free-solid-svg-icons';
import { useDispatch, useSelector } from 'react-redux';

function Converter() {

    const dispatch = useDispatch();
    const currency = useSelector((state) => state.Converter.currency) ;
    const amount = useSelector((state) => state.converter.amount)
    const fromcurrency = useSelector((state.converter.fromcurrency))

    return (
        <div className='w-25 border m-auto mt-5 rounded-3 main bg-black text-white'>
            <h2 className='text-center pt-3'>Currency converter</h2>
            <div className='mt-5 px-5'>
               <Dropdown
                    title='From'
               /> 
               <br/>
               <div className='text-center border text-center bg-white text-black w-25 rounded m-auto mb-4'>
                    <FontAwesomeIcon icon={faArrowDown} />
                    <FontAwesomeIcon icon={faArrowUp} />
               </div>
               <Dropdown 
                    title='To'
               />
            </div>
            <div className='mt-5 text-center'>
                <label htmlFor="" className='fs-5 p-4'>Amount</label>
                <input type="text" />
            </div>
            <p className='text-center fs-5'> 1 USD = 83.48 Indian Rupee</p>
        </div>

    )
}

export default Converter
